import "./App.css";
import { useState } from "react";
import Axios from "axios";

function App() {
  const [n, setN] = useState("");
  const [ln, setLn] = useState("");
  const [p, setP] = useState("");
  const [userList, setUserList] = useState([]);

  const getUser = () => {
    Axios.get("http://localhost:3333/User").then((response) => {
      setUserList(response.data);
    });
  };

  const addUser = () => {
    Axios.post("http://localhost:3333/create", {
      n: n,
      ln: ln,
      p: p,
    }).then(() => {
      setUserList([
        ...userList,
        {
          n: n,
          ln: ln,
          p: p,
        },
      ]);
    });
  };

  return (

    <div className="App container">
  <h2>Horizontal form</h2>
      <div className="information">
        <form action="">
          <div className="mb-3">
            <label className="form-label" htmlFor="name">
              Name:
            </label>
            <input
              type="text"
              className="form-control"
              placeholder="Enter name"
              onChange={(event) => {
                setN(event.target.value);
              }}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="age">ddd:</label>
            <input
              type="text"
              className="form-control"
              placeholder="e"
              onChange={(event) => {
                setLn(event.target.value);
              }}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="password">Password:</label>
            <input
              type="text"
              className="form-control"
              placeholder="password"
              onChange={(event) => {
                setP(event.target.value);
              }}
            />
          </div>
          <button onClick={addUser} className="btn btn-success">
            Add Employee
          </button>
        </form>
      </div>
      <hr />
      <div className="employees">
        <button className="btn btn-primary" onClick={getUser}>
          Show Employees
        </button>
        <br />
        <br />
        {userList.map((val, key) => {
          return (
            <div key={key} className="employee card">
              <div className="card-body text-left">
                <p className="card-text">Name: {val.n}</p>
                <p className="card-text">Lastname: {val.ln}</p>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
